class Parser extends BaseParser {
    computePrec(token) {
        let newPrec = this.operators[token.value]
        if (token.type == TokenTypes.EXPONENTIATION) {
            newPrec -= 1
        }
        return newPrec
    }
    /**
     * Prefix
     *    = ParenthesizedExpression
     *    / MaybeSpace
     *    / MaybeLabel
     *    / MaybeLabel
     *    / UnaryExpression
     *    / VariableOrFunctionExpression
     *    / NUMBER
     */
    /* @bookmark 1708430020 prefix */
    Prefix() {
        const colorToken = this.maybeEat(TokenTypes.LABEL)
        const prefix = this._Prefix()
        if (colorToken) {
            prefix.color = colorToken.value
        }
        // console.log(prefix); throw "throwing"
        return prefix
    }
    _Prefix(implicit) {

        if (this.lookahead == null) {
            throw 'unfortunately ... reached the end ... without a result'
        }
        if (this.lookahead.type === TokenTypes.SPACE) {
            // console.log('removing the preceding prefix space')
            this.eat()
        }

        if (this.lookahead.type === TokenTypes.PARENTHESIS_LEFT) {
            return this.ParenthesizedExpression()
            // it is necessary to pass a context
            // inside of parentheses, to do things differently
        }

        if (this.lookahead.type === TokenTypes.SUBTRACTION) {
            return this.UnaryExpression()
        }


        let token
        //  the lookahead is constantly redefiend
        // throw this.lookahead
        if (this.lookahead.type == TokenTypes.CHAR) {
            token = this.eat(this.lookahead.type)
            // console.log({token})
            // console.log(token); throw "token"
            if (this.lookahead == null) {
                throw 'cant be null'
            }
            else if (this.lookahead?.type == TokenTypes.NUMBER) {
                token.value += '^' + this.eat().value
                // console.log(token.value); throw "throwing"
                // const right = this.eat()
                // console.log(right); throw "right"
                // token = {
                    // type: 'BinaryExpression',
                    // operator: '^',
                    // left: token.value,
                    // right: right.value,
                    // left: token,
                    // right: right,

                // }
                // console.log(token); throw "throwing"
                // return token
            } else {
                throw 'needs to be followed by an exp'
            }
        }

        else if (this.lookahead.type == TokenTypes.NUMBER) {
            token = this.eat(TokenTypes.NUMBER)
            if (this.lookahead?.type == TokenTypes.CHAR) {
                token.value += this.eat().value
            }

            if (this.lookahead?.type == TokenTypes.NUMBER) {
                token.value += '^' + this.eat().value
            }
            // console.log('removing the next space')
            // this.implicit = false
            // this.eat()
            // throw ''
            // console.log(this.lookahead)
        }

        if (this.lookahead?.type === TokenTypes.SPACE) {
            // console.log('removing the next space')
            // console.log('implicit is over')
            this.implicit = false
            this.eat()
            // throw ''
            // console.log(this.lookahead)
        }
        const payload = {
            type: "Number",
            value: token.value
        }
        return payload
    }

    look() {
        console.log('looking', {s: this.input, cursor: this.tokenizer.cursor, remaining: this.input.slice(this.tokenizer.cursor)})
    }
     // * Infix
     // *    = (" " | "+" / "-" / "*" / "/" / "^") Expression
     // */
    Infix(left, operatorType, implicit) {
        let operatorToken = this.eat(operatorType)
        // console.log({operatorToken, implicit})
        // eating the exponent operator
        const prec = this.computePrec(operatorToken)
        // console.log(prec); throw "prec"
        // console.log({operatorToken})
        if (operatorToken.type == TokenTypes.EXPONENTIATION) {
            if (this.implicitNotAllowed) {
                
            } else {
                this.implicit = true
            }
            // console.log('starting implicit')
        }

        return {
            type: "BinaryExpression",
            operator: operatorToken.value,
            left,
            right: this.Expression(prec, implicit)
        }
    }
    Postfix(right, colorToken) {
        if (colorToken) {
            throw {right, colorToken}
        }
        return right
        
    }
    Expression(prec = 0, implicit = false) {


        let left = this.Prefix()
        while (prec < this.getPrecedence(this.lookahead)) {
            left = this.Infix(left, this.lookahead?.type)
        }
        return this.Postfix(left)
    }

    ParenthesizedExpression() {
        this.implicitNotAllowed = true
        this.eat(TokenTypes.PARENTHESIS_LEFT)
        const expression = this.Expression()
        this.eat(TokenTypes.PARENTHESIS_RIGHT)
        this.implicitNotAllowed = false
        return expression
    }
}
